mapper.update:
		update zj_order2log set order2id=#{id}

		
			,state=#{state}
		
		
			,nprice=#{nprice}
		
		
			,oprice=#{oprice}
		
		
			,amount=#{amount}
		
		
			,type=#{type}
		
		
			,utime=#{time}
		

		where order2id=#{id}

		update zj_goods set
		oamount=1             
		
			

				,storage=storage-#{amount}
			
			
				,storage=storage+#{amount}
			

		

		where id=#{goodsid}

		update zj_goods set
		oamount=1            
		
			

				,storage=storage-#{amount}
			
			
				,storage=storage+#{amount}
			

		

		where id=#{goodsid}
